import React, { useState } from 'react';
import Picture from '../../assets/header.png'
import data from './data'
import { IoIosColorPalette } from "react-icons/io";
import { SketchPicker } from 'react-color';
import './navBar.css'

const NavBar = ({ onThemeToggle, onColorChange }) => {

    const [displayColorPicker, setDisplayColorPicker] = useState(false);

    const colorOptions = ['#FFA07A', '#FA8072', '#E9967A', '#F08080', '#CD5C5C'];

    const handleColorClick = (color) => {
        onColorChange(color);
        setDisplayColorPicker(false);
    };


    return (
        <nav>
            <div className="container nav__container" >
                <div className="theme-toggle">
                </div>
                <a href="index.html" className='nav__logo'>
                    <img src={Picture} alt="Solo Picture" />
                </a>
                <ul className='nav__menu'>
                    {
                        data.map(item => <li key={item.id}> <a href={item.link}>{item.title}</a> </li>)
                    }
                </ul>
                <IoIosColorPalette onClick={() => setDisplayColorPicker(!displayColorPicker)} />
                {displayColorPicker && (
                    <div className="color-popup">
                        {colorOptions.map((color, index) => (
                            <div
                                key={index}
                                className="color-swatch"
                                style={{ backgroundColor: color }}
                                onClick={() => handleColorClick(color)}
                            />
                        ))}
                    </div>
                )}
                <button onClick={onThemeToggle}>Toggle Theme</button>
            </div>
        </nav>
    )
}

export default NavBar;
