import NavBar from './sections/navBar/navBar';
import About from './sections/about/About';
import Education from './sections/education/Education';
import Certifications from './sections/certifications/Certifications';
import Contact from './sections/contact/Contact';
import WorkExperience from './sections/workExperience/workExperience';
import Projects from './sections/projects/projects';
import Header from './sections/header/Header';
import FloatingNav from './sections/floatingNav/floatingNav';
import React, { useState } from 'react';
import './App.css';

const App = () => {
    const [theme, setTheme] = useState('light-theme'); // Default theme

    const handleThemeChange = (newTheme) => {
        setTheme(newTheme);
    };

    // Note: Make sure your buttons or theme-changing mechanism is placed within the UI where users can interact with them. 
    // For the sake of this example, I'm placing them at the top of the main content.

    return (
        <div className={`App ${theme}`}>
            <NavBar />
            <Header />
            <button onClick={() => handleThemeChange('light-theme')}>Light Theme</button>
            <button onClick={() => handleThemeChange('dark-theme')}>Dark Theme</button>
            <About />
            <Education />
            <Certifications />
            <WorkExperience />
            <Projects />
            <Contact />
            <FloatingNav />
        </div>
    );
}

export default App;
